$(function(){updateEndTime()});function updateEndTime(){var date=new Date,time=date.getTime();$(".details-times").each(function(i){var endDate=this.getAttribute("endTime"),endDate1=eval("new Date("+endDate.replace(/\d+(?=-[^-]+$)/,function(e){return parseInt(e,10)-1}).match(/\d+/g)+")"),endTime=endDate1.getTime(),lag=(endTime-time)/1e3;if(lag>0){var second=Math.floor(lag%60),minite=Math.floor(lag/60%60),hour=Math.floor(lag/3600%24),day=Math.floor(lag/3600/24);$(this).html(day+"天"+hour+"小时"+minite+"分"+second+"秒")}}),setTimeout("updateEndTime()",1e3)}
